mapper.select:
	select count(*) from t_comment
	where 1=1 
  
    and ID = #{id,jdbcType=DECIMAL}
  
  
    and content like '%${content}%' 
  
  
    and user_id = #{user_id,jdbcType=VARCHAR} 
  
  
    and news_id = #{news_id,jdbcType=VARCHAR} 
  
  
    and parent_id = #{news_id,jdbcType=VARCHAR}

select
 
from t_comment TB
where 1=1 

  and ID = #{id,jdbcType=DECIMAL}


  and content like '%${content}%' 


  and user_id = #{user_id,jdbcType=VARCHAR} 


  and news_id = #{news_id,jdbcType=VARCHAR} 


  and parent_id = #{news_id,jdbcType=VARCHAR} 

order by id desc

	   
